                <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">×</span>
                </button>
            </div>
            <div class="modal-body">
                <div class="form-group">
                    <label for="user">帳號</label>
                    <input type="text" class="form-control" name="user" id="user">
                </div>
                <div class="form-group">
                    <label for="username">真實姓名</label>
                    <input type="text" class="form-control" name="username" id="username">
                </div>
                <div class="form-group">
                    <label for="password">密碼</label>
                    <input type="password" class="form-control" name="password" id="password">
                </div>
                <div class="form-group">
                    <label for="isadmin">是否為管理員</label>
                    <select name="isadmin" id="isadmin" class="form-control">
                        <option value="1">是</option>
                        <option value="0">否</option>
                    </select>
                </div>
                <div class="form-group">
                    <label for="level">等級</label>
                    <input type="text" class="form-control" name="level" id="level">
                </div>
                <div class=" form-group">
                    <label for="cellphone">手機號</label>
                    <input type="text" class="form-control" name="cellphone" id="cellphone">
                </div>
                <div class=" form-group">
                    <label for="address">gender</label>
                    <select name="gender" id="gender" class="form-control">
                        <option value="男">男</option>
                        <option value="女">女</option>
                    </select>
                </div>
            </div>
            <div class=" modal-footer">
                <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                <button type="button" class="btn btn-primary create" dataid="" style="">Save changes</button>
                <button type="button" class="btn btn-primary update" dataid="" style="display: none;">update changes</button>
            </div>
        </div>
    </div>
</div>
<script>
$("#create_modal").on('click', function() {
    $("#exampleModalLabel").html('新增帳號');
    $("input").val('');
    $(".update").hide();
    $(".create").show();
});


$(".create").on('click', function() {


    var $user = $("#user").val();

    $.ajax({
            url: "http://teach.ukyo.idv.tw/user",
            type: 'POST',
            data: { user: $user, username: $("#username").val(), password: $("input[name='password']").val(), isadmin: $("#isadmin").val(), level: $("#level").val(), cellphone: $("cellphone").val(), gender: $("#gender").val() },
            headers: {
                'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
            }
        })
        .done(function(data) {

            if (data.status == 200) {

                alert('success');
                location.reload();
            }

            console.log("success");
        })
});
//*****

function update(data) {
    $(".create").hide();
    $(".update").show();

    console.log(data);
    $("#exampleModalLabel").html('修改帳號');
    $("#user").val(data.user);
    $("#username").val(data.username);
    $("#isadmin").val(data.isadmin).prop('selected', true);
    $("#level").val(data.lv);
    $("#cellphone").val(data.cellphone);
    $("#gender").val(data.gender).prop('selected', true);



    $(".update").on('click', function() {
        var url = "http://teach.ukyo.idv.tw/user/" + data.id
        $.ajax({
                url: url,
                type: 'PUT',
                dataType: 'json',
                data: { user: $("#user").val(), username: $("#username").val(), password: $("input[name='password']").val(), isadmin: $("#isadmin").val(), level: $("#level").val(), cellphone: $("cellphone").val(), gender: $("#gender").val() },
                headers: {
                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                }

            })
            .done(function() {
                console.log("success");
            })
            .fail(function() {
                console.log("error");
            })
            .always(function() {
                console.log("complete");
            });


    });





}

</script>



<div class="modal-backdrop fade show"></div></body></html>